import React from "react";
import { Link } from "react-router-dom";
import "../styles/Home.css";

function Home() {
  return (
    <div className="home-container">
      {/* Encabezado principal */}
      <header className="hero-section">
        <h1>Gestiona tu Portafolio con Stock by Choice</h1>
        <p>
          Descubre herramientas intuitivas para gestionar tus inversiones y seleccionar las mejores acciones.
        </p>
        <Link to="/register" className="cta-button">Comienza Ahora</Link>
      </header>

      {/* Secciones destacadas */}
      <section className="features-section">
        <div className="feature-card">
          <h2>📊 Gestiona tu Portafolio</h2>
          <p>Administra y controla todas tus inversiones en un solo lugar.</p>
          <Link to="/portfolio" className="feature-link">Ver Portafolio</Link>
        </div>

        <div className="feature-card">
          <h2>📈 Selecciona Acciones</h2>
          <p>Consulta análisis y recomendaciones para tus acciones favoritas.</p>
          <Link to="/stocks" className="feature-link">Explora Stocks</Link>
        </div>

        <div className="feature-card">
          <h2>🔒 Seguridad Garantizada</h2>
          <p>Tu información y datos están protegidos con la mejor seguridad.</p>
          <Link to="/login" className="feature-link">Inicia Sesión</Link>
        </div>
      </section>
    </div>
  );
}

export default Home;
