import React from "react";
import { Link } from "react-router-dom";
import "../styles/Home.css";
import techImage1  from "../images/ai.jpg";
import techImage2  from "../images/ai.jpg";
import techImage3  from "../images/ai.jpg";
import heroImage from "../images/hero-bg.png"; // Imagen de fondo

function Home() {
  return (
    <div className="home-container">
      {/* Sección principal con imagen de fondo */}
      <section
        className="hero-section"
        style={{
          backgroundImage: `url(${heroImage})`,
        }}
      >
        <div className="hero-content">
          <h1 className="hero-title">Launch Your Financial Future Today</h1>
          <p className="hero-subtitle">Your choice, our insights.</p>
          <button className="hero-button">Try!</button>
        </div>
      </section>

      {/* Sección de "Our Technology" */}
      <section className="technology-section">
        <h2 className="section-title">Our Technology</h2>
        <div className="tech-cards">
          {/* Tarjeta 1 */}
          <div className="tech-card">
            <img src={techImage1} alt="Technology 1" className="tech-image" />
            <p className="tech-description">Advanced analytics for portfolio optimization.</p>
          </div>

          {/* Tarjeta 2 */}
          <div className="tech-card">
            <img src={techImage2} alt="Technology 2" className="tech-image" />
            <p className="tech-description">Real-time stock recommendations powered by AI.</p>
          </div>

          {/* Tarjeta 3 */}
          <div className="tech-card">
            <img src={techImage3} alt="Technology 3" className="tech-image" />
            <p className="tech-description">Intuitive tools for risk management and growth.</p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
